mapper.select:
select
    count(1)
from e_jgsb
where
    lrrq=#{closeDate}
  and lsh_id=#{userId}
  and ggbs=#{cigaretteNo}

select
    count(1)
from e_jgsb_bsc
where
    lrrq=#{closeDate}
  and lsh_id=#{userId}
  and ggbs=#{cigaretteNo}

mapper.update:
update e_jgsb

    xl=#{marketPrice},
    gjl=#{purchasePrice},
    kc=#{cartonPrice},
    tjjg=#{packPrice},

    gxsj=now(),


where
ggbs = #{cigaretteNo}
and lsh_id=#{userId}
and lrrq=#{closeDate}

update e_jgsb_bsc

    jsjyj=#{marketPrice},
    jssgj=#{purchasePrice},
    ztlsj=#{cartonPrice},
    dblsj=#{packPrice},
    zt=#{saleStatus},
    hyqk=#{stockStatus},
    gxsj=now(),
    jy=#{suggestion}

where
ggbs = #{cigaretteNo}
and lsh_id=#{userId}
and lrrq=#{closeDate}

update e_jgsb
set bzw=1, sbsj=now()
where
ggbs in

    #{item.cigaretteNo}

and lsh_id in

    #{item.userId}

and lrrq in

    #{item.closeDate}

update e_jgsb_bsc
set bzw=1, sbsj=now()
where
ggbs in

    #{item.cigaretteNo}

and lsh_id in

    #{item.userId}

and lrrq in

    #{item.closeDate}
